import pytest

from ethereum_test_exceptions import EOFException
from ethereum_test_types.eof.v1 import Container, Section
from ethereum_test_tools.vm.opcode import Opcodes as Op

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-663.md"
REFERENCE_SPEC_VERSION = "b658bb87fe039d29e9475d5cfaebca9b92e0fca2"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="callf_stack_overflow_0",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(
                    code=Op.PUSH1[1] * 512 + Op.CALLF[1] + Op.POP * 512 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=512,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="callf_stack_overflow_1",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(
                    code=Op.PUSH1[1] * 513 + Op.CALLF[1] + Op.POP * 513 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=513,
                ),
            ],
            expected_bytecode="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="callf_stack_overflow_2",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[1] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
            ],
            expected_bytecode="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="callf_stack_overflow_3",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[2] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 + Op.POP + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="callf_stack_overflow_4",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[2] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 * 2 + Op.POP * 2 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=2,
                ),
            ],
            expected_bytecode="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
    ],
    ids=lambda x: x.name,
)
def test_eof_validation(eof_test, container):
    eof_test(container=container)
