import pytest

from ethereum_test_exceptions import EOFException
from ethereum_test_types.eof.v1 import Container

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-663.md"
REFERENCE_SPEC_VERSION = "b658bb87fe039d29e9475d5cfaebca9b92e0fca2"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="callf_stack_overflow_variable_stack_0",
            raw_bytes="",
        ),
        Container(
            name="callf_stack_overflow_variable_stack_1",
            raw_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="callf_stack_overflow_variable_stack_2",
            raw_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="callf_stack_overflow_variable_stack_3",
            raw_bytes="",
        ),
        Container(
            name="callf_stack_overflow_variable_stack_4",
            raw_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="callf_stack_overflow_variable_stack_5",
            raw_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
    ],
    ids=lambda x: x.name,
)
def test_eof_validation(eof_test, container):
    eof_test(container=container)
