import pytest

from ethereum_test_exceptions import EOFException
from ethereum_test_types.eof.v1 import Container, Section
from ethereum_test_tools.vm.opcode import Opcodes as Op

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-663.md"
REFERENCE_SPEC_VERSION = "b658bb87fe039d29e9475d5cfaebca9b92e0fca2"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="jumpf_stack_overflow_variable_stack_0",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 511 + Op.JUMPF[0],
                    max_stack_height=512,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="jumpf_stack_overflow_variable_stack_1",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 511 + Op.JUMPF[1],
                    max_stack_height=512,
                ),
                Section.Code(code=Op.PUSH0 * 515 + Op.STOP, max_stack_height=515),
            ],
            expected_bytecode="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="jumpf_stack_overflow_variable_stack_2",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 511 + Op.JUMPF[1],
                    max_stack_height=512,
                ),
                Section.Code(code=Op.PUSH0 * 514 + Op.STOP, max_stack_height=514),
            ],
            expected_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="jumpf_stack_overflow_variable_stack_3",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 1022 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.PUSH0 + Op.STOP, max_stack_height=1),
            ],
            expected_bytes="",
        ),
        Container(
            name="jumpf_stack_overflow_variable_stack_4",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 1022 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.PUSH0 * 5 + Op.STOP, max_stack_height=5),
            ],
            expected_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="jumpf_stack_overflow_variable_stack_5",
            sections=[
                Section.Code(
                    code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 * 1022 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.PUSH0 * 2 + Op.STOP, max_stack_height=2),
            ],
            expected_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
    ],
    ids=lambda x: x.name,
)
def test_eof_validation(eof_test, container):
    eof_test(container=container)
