import pytest

from ethereum_test_exceptions import EOFException
from ethereum_test_types.eof.v1 import Container, Section
from ethereum_test_tools.vm.opcode import Opcodes as Op

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-663.md"
REFERENCE_SPEC_VERSION = "b658bb87fe039d29e9475d5cfaebca9b92e0fca2"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="jumpf_with_inputs_stack_overflow_0",
            sections=[
                Section.Code(
                    code=Op.PUSH0 * 1023 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 + Op.STOP,
                    code_inputs=2,
                    max_stack_height=3,
                ),
            ],
            expected_bytes="",
        ),
        Container(
            name="jumpf_with_inputs_stack_overflow_1",
            sections=[
                Section.Code(
                    code=Op.PUSH0 * 1023 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 * 2 + Op.STOP,
                    code_inputs=2,
                    max_stack_height=4,
                ),
            ],
            expected_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
        Container(
            name="jumpf_with_inputs_stack_overflow_2",
            sections=[
                Section.Code(
                    code=Op.PUSH0 * 1024 + Op.JUMPF[1],
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 + Op.STOP,
                    code_inputs=2,
                    max_stack_height=3,
                ),
            ],
            expected_bytes="",
            validity_error=EOFException.STACK_OVERFLOW,
        ),
    ],
    ids=lambda x: x.name,
)
def test_eof_validation(eof_test, container):
    eof_test(container=container)
