import pytest
from ethereum_test_exceptions import EOFException
from ethereum_test_types.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = 'EIPS/eip-663.md'
REFERENCE_SPEC_VERSION = 'b658bb87fe039d29e9475d5cfaebca9b92e0fca2'

@pytest.mark.parametrize(
    'container',
    [
        Container(
            name='max_stack_height_0',
            raw_bytes="",
        ),
        Container(
            name='max_stack_height_1',
            raw_bytes="",
        ),
        Container(
            name='max_stack_height_2',
            raw_bytes="",
            validity_error=EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT,
        ),
        Container(
            name='max_stack_height_3',
            raw_bytes="",
            validity_error=EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT,
        ),
        Container(
            name='max_stack_height_4',
            raw_bytes="",
            validity_error=EOFException.UNREACHABLE_CODE_SECTIONS,
        ),
        Container(
            name='max_stack_height_5',
            raw_bytes="",
            validity_error=EOFException.INVALID_MAX_STACK_HEIGHT,
        ),
        Container(
            name='max_stack_height_6',
            raw_bytes="ef0001010004020001000804000000008000016000e10002600100",
        ),
        Container(
            name='max_stack_height_7',
            raw_bytes="ef0001010004020001000604000000008000016000e1fffd00",
            validity_error=EOFException.STACK_HEIGHT_MISMATCH,
        ),
        Container(
            name='max_stack_height_8',
            raw_bytes="ef0001010004020001000704000000008000016000e200fffc00",
            validity_error=EOFException.STACK_HEIGHT_MISMATCH,
        ),
    ],
    ids=lambda x: x.name,
)
def test_eof_validation(eof_test, container):
    eof_test(container=container)
