"""EOF v1 validation code - Exported from evmone unit tests."""

import pytest

from ethereum_test_tools import EOFException, EOFTestFiller
from ethereum_test_tools import Opcodes as Op
from ethereum_test_tools.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-3540.md"
REFERENCE_SPEC_VERSION = "8dcb0a8c1c0102c87224308028632cc986a61183"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="max_stack_height_0",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="max_stack_height_1",
            sections=[
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.POP * 1023 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
            ],
            expected_bytecode="",
        ),
        Container(
            name="max_stack_height_2",
            sections=[
                Section.Code(code=Op.PUSH1[1] * 1024 + Op.STOP, max_stack_height=1024),
            ],
            expected_bytecode="",
            validity_error=[EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT],
        ),
        Container(
            name="max_stack_height_3",
            sections=[
                Section.Code(
                    code=Op.PUSH1[1] * 1024 + Op.CALLF[1] + Op.POP * 1024 + Op.STOP,
                    max_stack_height=1024,
                ),
                Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
            ],
            expected_bytecode="",
            validity_error=[EOFException.MAX_STACK_HEIGHT_ABOVE_LIMIT],
        ),
        Container(
            name="max_stack_height_4",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 1024 + Op.POP * 1024 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.INVALID_MAX_STACK_HEIGHT],
        ),
        Container(
            name="max_stack_height_5",
            sections=[
                Section.Code(
                    code=Op.PUSH1[1] * 1024 + Op.CALLF[1] + Op.POP * 1024 + Op.STOP,
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.RETF, code_outputs=0, max_stack_height=0),
            ],
            expected_bytecode="",
            validity_error=[EOFException.INVALID_MAX_STACK_HEIGHT],
        ),
        Container(
            name="max_stack_height_6",
            sections=[
                Section.Code(
                    code=Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH1[1] + Op.STOP, max_stack_height=1
                ),
            ],
            expected_bytecode="ef0001010004020001000804000000008000016000e10002600100",
        ),
        Container(
            name="max_stack_height_7",
            sections=[
                Section.Code(code=Op.PUSH1[0] + Op.RJUMPI[-3] + Op.STOP, max_stack_height=1),
            ],
            expected_bytecode="ef0001010004020001000604000000008000016000e1fffd00",
            validity_error=[EOFException.STACK_HEIGHT_MISMATCH],
        ),
        Container(
            name="max_stack_height_8",
            sections=[
                Section.Code(code=Op.PUSH1[0] + Op.RJUMPV[-4] + Op.STOP, max_stack_height=1),
            ],
            expected_bytecode="ef0001010004020001000704000000008000016000e200fffc00",
            validity_error=[EOFException.STACK_HEIGHT_MISMATCH],
        ),
    ],
    ids=lambda c: c.name,
)
def test_max_stack_height(
    eof_test: EOFTestFiller,
    container: Container,
):
    """Test the maximum stack height does not exceed the limit (1023)."""
    eof_test(
        container=container,
        expect_exception=container.validity_error,
    )
