"""EOF v1 validation code - Exported from evmone unit tests."""

import pytest

from ethereum_test_tools import EOFException, EOFTestFiller
from ethereum_test_tools import Opcodes as Op
from ethereum_test_tools.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-4750.md"
REFERENCE_SPEC_VERSION = "14400434e1199c57d912082127b1d22643788d11"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="max_arguments_count_0",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP),
                Section.Code(code=Op.RETF, code_inputs=128, max_stack_height=128),
            ],
            expected_bytecode="ef000101000802000200040001040000000080000080800080e3000100e4",
            validity_error=[EOFException.INPUTS_OUTPUTS_NUM_ABOVE_LIMIT],
        ),
        Container(
            name="max_arguments_count_1",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=127),
                Section.Code(
                    code=Op.PUSH1[1] * 127 + Op.RETF,
                    code_outputs=127,
                    max_stack_height=127,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="max_arguments_count_2",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=129),
                Section.Code(
                    code=Op.PUSH1[1] * 129 + Op.RETF,
                    code_outputs=129,
                    max_stack_height=129,
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.INPUTS_OUTPUTS_NUM_ABOVE_LIMIT],
        ),
        Container(
            name="max_arguments_count_3",
            sections=[
                Section.Code(
                    code=Op.PUSH1[1] * 128 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=128,
                ),
                Section.Code(
                    code=Op.POP * 128 + Op.RETF,
                    code_inputs=128,
                    code_outputs=0,
                    max_stack_height=128,
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.INPUTS_OUTPUTS_NUM_ABOVE_LIMIT],
        ),
    ],
    ids=lambda c: c.name,
)
def test_max_arguments_count(
    eof_test: EOFTestFiller,
    container: Container,
):
    """Test the maximum arguments count does not exceed the limit (127)."""
    eof_test(
        container=container,
        expect_exception=container.validity_error,
    )
