"""EOF v1 validation code - Exported from evmone unit tests."""

import pytest

from ethereum_test_tools import EOFException, EOFTestFiller
from ethereum_test_tools import Opcodes as Op
from ethereum_test_tools.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-5450.md"
REFERENCE_SPEC_VERSION = "f20b164b00ae5553f7536a6d7a83a0f254455e09"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="callf_stack_overflow_0",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 512 + Op.CALLF[1] + Op.POP * 512 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=512,
                ),
            ],
            expected_bytecode="",
        ),
        Container(
            name="callf_stack_overflow_1",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 513 + Op.CALLF[1] + Op.POP * 513 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=513,
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.STACK_OVERFLOW],
        ),
        Container(
            name="callf_stack_overflow_2",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[1] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.STACK_OVERFLOW],
        ),
        Container(
            name="callf_stack_overflow_3",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[2] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
                Section.Code(code=Op.PUSH0 + Op.POP + Op.RETF, code_outputs=0, max_stack_height=1),
            ],
            expected_bytecode="",
        ),
        Container(
            name="callf_stack_overflow_4",
            sections=[
                Section.Code(code=Op.CALLF[1] + Op.STOP, max_stack_height=0),
                Section.Code(
                    code=Op.PUSH1[1] * 1023 + Op.CALLF[2] + Op.POP * 1023 + Op.RETF,
                    code_outputs=0,
                    max_stack_height=1023,
                ),
                Section.Code(
                    code=Op.PUSH0 * 2 + Op.POP * 2 + Op.RETF, code_outputs=0, max_stack_height=2
                ),
            ],
            expected_bytecode="",
            validity_error=[EOFException.STACK_OVERFLOW],
        ),
    ],
    ids=lambda c: c.name,
)
def test_callf_stack_overflow(
    eof_test: EOFTestFiller,
    container: Container,
):
    """Test CALLF reaching stack overflow."""
    eof_test(
        container=container,
        expect_exception=container.validity_error,
    )
