"""EOF v1 validation code - Exported from evmone unit tests."""

import pytest
from ethereum_test_tools import EOFTestFiller, EOFException, Opcodes as Op
from ethereum_test_tools.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-5450.md"
REFERENCE_SPEC_VERSION = "f20b164b00ae5553f7536a6d7a83a0f254455e09"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_0",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.POP * 2 + Op.RETF,
                    code_inputs=2,
                    code_outputs=0,
                    max_stack_height=2),
            ],
            expected_bytecode="",
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_1",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH1[1] + Op.POP + Op.RETF,
                    code_inputs=3,
                    code_outputs=3,
                    max_stack_height=4),
            ],
            expected_bytecode="",
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_2",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 4 + Op.RETF,
                    code_inputs=3,
                    code_outputs=7,
                    max_stack_height=7),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_3",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 2 + Op.RETF,
                    code_inputs=3,
                    code_outputs=5,
                    max_stack_height=5),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_4",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 4 + Op.POP * 2 + Op.RETF,
                    code_inputs=3,
                    code_outputs=3,
                    max_stack_height=7),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_5",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 2 + Op.POP * 2 + Op.RETF,
                    code_inputs=3,
                    code_outputs=3,
                    max_stack_height=5),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_6",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1021 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 3 + Op.POP * 5 + Op.RETF,
                    code_inputs=2,
                    code_outputs=0,
                    max_stack_height=5),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_7",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1021 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 + Op.POP * 3 + Op.RETF,
                    code_inputs=2,
                    code_outputs=0,
                    max_stack_height=3),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_8",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 4 + Op.POP * 6 + Op.RETF,
                    code_inputs=2,
                    code_outputs=0,
                    max_stack_height=6),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),
        Container(
            name="callf_with_inputs_stack_overflow_variable_stack_9",
            sections=[
                Section.Code(code=Op.PUSH0 + Op.PUSH1[0] + Op.RJUMPI[2] + Op.PUSH0 *
                    2 + Op.PUSH1[1] * 1020 + Op.CALLF[1] + Op.STOP,
                    max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 2 + Op.POP * 4 + Op.RETF,
                    code_inputs=2,
                    code_outputs=0,
                    max_stack_height=4),
            ],
            expected_bytecode="",
            validity_error=[
                EOFException.STACK_OVERFLOW
            ],
          ),

    ],
    ids = lambda c: c.name,
)
def test_callf_with_inputs_stack_overflow_variable_stack(
    eof_test: EOFTestFiller,
    container: Container,
):
    """Test CALLF to functions with inputs reaching stack overflow (variable stack)."""
    eof_test(
        container=container,
        expect_exception=container.validity_error,
    )
