"""EOF v1 validation code - Exported from evmone unit tests."""

import pytest

from ethereum_test_tools import EOFException, EOFTestFiller
from ethereum_test_tools import Opcodes as Op
from ethereum_test_tools.eof.v1 import Container, Section

REFERENCE_SPEC_GIT_PATH = "EIPS/eip-5450.md"
REFERENCE_SPEC_VERSION = "f20b164b00ae5553f7536a6d7a83a0f254455e09"


@pytest.mark.parametrize(
    "container",
    [
        Container(
            name="jumpf_with_inputs_stack_overflow_0",
            sections=[
                Section.Code(code=Op.PUSH0 * 1023 + Op.JUMPF[1], max_stack_height=1023),
                Section.Code(code=Op.PUSH0 + Op.STOP, code_inputs=2, max_stack_height=3),
            ],
            expected_bytecode="",
        ),
        Container(
            name="jumpf_with_inputs_stack_overflow_1",
            sections=[
                Section.Code(code=Op.PUSH0 * 1023 + Op.JUMPF[1], max_stack_height=1023),
                Section.Code(code=Op.PUSH0 * 2 + Op.STOP, code_inputs=2, max_stack_height=4),
            ],
            expected_bytecode="",
            validity_error=[EOFException.STACK_OVERFLOW],
        ),
        Container(
            name="jumpf_with_inputs_stack_overflow_2",
            sections=[
                Section.Code(code=Op.PUSH0 * 1024 + Op.JUMPF[1], max_stack_height=1023),
                Section.Code(code=Op.PUSH0 + Op.STOP, code_inputs=2, max_stack_height=3),
            ],
            expected_bytecode="",
            validity_error=[EOFException.STACK_OVERFLOW],
        ),
    ],
    ids=lambda c: c.name,
)
def test_jumpf_with_inputs_stack_overflow(
    eof_test: EOFTestFiller,
    container: Container,
):
    """Test JUMPF with inputs reaching stack overflow."""
    eof_test(
        container=container,
        expect_exception=container.validity_error,
    )
